import Image, { ImageProps } from 'next/image';

interface OptimizedImageProps extends Omit<ImageProps, 'src'> {
  src: string;
  alt: string;
  width?: number;
  height?: number;
  className?: string;
  priority?: boolean;
}

export function OptimizedImage({
  src,
  alt,
  width = 800,
  height = 600,
  className = "",
  priority = false,
  ...props
}: OptimizedImageProps) {
  // Check if it's a CDN URL and optimize accordingly
  const isCloudFrontImage = src.includes('d1btycpz485vry.cloudfront.net');
  
  return (
    <div className={`relative overflow-hidden rounded-lg ${className}`}>
      <Image
        src={src}
        alt={alt}
        width={width}
        height={height}
        priority={priority}
        className="w-full h-auto object-cover"
        quality={90}
        placeholder={isCloudFrontImage ? "blur" : "empty"}
        blurDataURL={isCloudFrontImage ? 
          "data:image/jpeg;base64," 
          : undefined}
        {...props}
      />
    </div>
  );
}

export default OptimizedImage;
